import twilio from 'twilio';

const accountSid = 'AC684cc852c918cef4da6975211004eca9';
const authToken = '499cdcf698f06ea6a5fe91388381d2f2';
const client = twilio(accountSid, authToken);


export async function sendTextMessage(toPhoneNumber, message) {
    const fromNumber = "+18559314591";

    try {
        const result = await client.messages.create({
            body: message,
            from: fromNumber,
            to: toPhoneNumber
        });
        console.log(result.sid);
    } catch (error) {
        console.error(error);
    }
}